pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/Math.sol";

contract dOpticonBorrower {
    using Math for uint256;

    uint256 public totalDeposited;
    uint256 public totalBorrowed;
    uint256 public lastAction;
    uint256 public ONE_YEAR = 365 * 24 * 60 * 60;
    uint256 public _ibTokenPrice = 1e18;
    uint256 public ibTokenSupply = 0;

    function increaseDeposit(uint256 value) public {
        update();
        totalDeposited += value * 1e18;
        lastAction = block.timestamp;
        update();
    }

    function increaseBorrowed(uint256 value) public {
        update();
        totalBorrowed += value * 1e18;
        lastAction = block.timestamp;
        update();
    }

    function getUtilization() public view returns (uint256) {
        return ((totalBorrowed * 1e18) / totalDeposited);
    }

    function getBorrowAPR() public view returns (uint256) {
        uint256 utilization = getUtilization();
        uint256 sqrt = 1e18 - (1e36 - utilization**2).sqrt();
        return (sqrt * 3) / 2;
    }

    function getPendingInterest() public view returns (uint256) {
        uint256 apr = getBorrowAPR();
        uint256 pendingInterest = (apr *
            totalBorrowed *
            (block.timestamp - lastAction)) / (ONE_YEAR * 1e18);
        return pendingInterest;
    }

    function ibTokenPrice() public view returns (uint256) {
        uint256 pendingInterest = getPendingInterest();
        return ((totalDeposited + pendingInterest) * 1e18) / totalDeposited;
    }

    function update() public {
        uint256 pendingInterest = getPendingInterest();
        _ibTokenPrice =
            ((totalDeposited + pendingInterest) * 1e18) /
            totalDeposited;
        lastAction = block.timestamp;
    }
}
